              </>
            )}
          </Button>
        </div>
      </motion.div>

      {/* Metrics Overview */}
      <motion.div variants={itemVariants}>
        {isLoading ? (
          <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
            {[...Array(4)].map((_, i) => (
              <div key={i} className="space-y-3 p-6 rounded-lg border border-primary/30 bg-card">
