  color: rgb(var(--neon-pink));
}

.text-gold {
  color: rgb(var(--gold));
}

.text-cyan {
  color: rgb(var(--cyan));
}

.text-blue-light {
  color: rgb(var(--blue-light));
}

/* Success, Warning, Error text */
.text-success {
  color: rgb(var(--success));
}

.text-warning {
  color: rgb(var(--warning));
}

.text-error {
  color: rgb(var(--destructive));
}

/* Border Radius Utilities */
.radius-sm {
  border-radius: 8px;
}

.radius-md {
  border-radius: 12px;
}

.radius-lg {
  border-radius: 16px;
}

.radius-xl {
  border-radius: 20px;
}

.radius-2xl {
  border-radius: 24px;
}

/* ===================================
   Shimmer Loading Animation
   =================================== */

@keyframes shimmer {
  0% {
    background-position: -1000px 0;
  }
  100% {
    background-position: 1000px 0;
  }
}

.shimmer {
  background: linear-gradient(
    90deg,
    rgba(255, 107, 0, 0.05) 0%,
    rgba(255, 107, 0, 0.15) 50%,
    rgba(255, 107, 0, 0.05) 100%
  );
  background-size: 1000px 100%;
  animation: shimmer 2s infinite linear;
}

