    .from('gmb_accounts')
    .update({ last_sync: new Date().toISOString() })
    .eq('id', accountId)
    .eq('user_id', user.id)

  if (error) {
    return { success: false, error: error.message }
  }

  revalidatePath('/accounts')
  return { success: true, error: null }
}

