import { NextRequest, NextResponse } from 'next/server';
import { createClient, createAdminClient } from '@/lib/supabase/server';

export const dynamic = 'force-dynamic';

const GOOGLE_AUTH_URL = 'https://accounts.google.com/o/oauth2/v2/auth';
const SCOPES = [
  'https://www.googleapis.com/auth/business.manage',
  'https://www.googleapis.com/auth/userinfo.email',
  'https://www.googleapis.com/auth/userinfo.profile',
];

export async function POST(request: NextRequest) {
  console.log('[Create Auth URL] Creating Google OAuth URL...');

  try {
    const supabase = await createClient();

    // Get authenticated user
    const { data: { user }, error: authError } = await supabase.auth.getUser();
    if (authError || !user) {
      console.error('[Create Auth URL] User not authenticated:', authError);
      return NextResponse.json(
        { error: 'Unauthorized' },
        { status: 401 }
      );
    }

    console.log('[Create Auth URL] User authenticated:', user.id);

    // Get OAuth configuration
    const clientId = process.env.GOOGLE_CLIENT_ID;
    const redirectUri = process.env.GOOGLE_REDIRECT_URI || 
      `${process.env.NEXT_PUBLIC_BASE_URL}/api/gmb/oauth-callback`;

    if (!clientId) {
      console.error('[Create Auth URL] Missing Google OAuth configuration');
      return NextResponse.json(
        { error: 'Server configuration error: Missing Google OAuth credentials' },
        { status: 500 }
      );
    }

    // Generate random state for security
    const state = crypto.randomUUID();
    console.log('[Create Auth URL] Generated state:', state);

    // Calculate expiry time (30 minutes from now)
    const expiresAt = new Date();
    expiresAt.setMinutes(expiresAt.getMinutes() + 30);

    // Save state to database using admin client to bypass RLS
    // (We've already authenticated the user above with getUser())
    console.log('[Create Auth URL] Attempting to insert state:', {
      state,
      user_id: user.id,
      expires_at: expiresAt.toISOString(),
      used: false,
    });

    const adminClient = createAdminClient();
    const { data: insertData, error: stateError } = await adminClient
      .from('oauth_states')
      .insert({
        state,
        user_id: user.id,
        expires_at: expiresAt.toISOString(),
        used: false,
      })
      .select();

    if (stateError) {
      console.error('[Create Auth URL] ===== ERROR SAVING STATE =====');
      console.error('[Create Auth URL] Full error object:', JSON.stringify(stateError, null, 2));
      console.error('[Create Auth URL] Error code:', stateError.code);
      console.error('[Create Auth URL] Error message:', stateError.message);
      console.error('[Create Auth URL] Error details:', stateError.details);
      console.error('[Create Auth URL] Error hint:', stateError.hint);
      console.error('[Create Auth URL] ================================');

      return NextResponse.json(
        { 
          error: 'Failed to save OAuth state', 
          message: stateError.message,
          code: stateError.code,
          hint: stateError.hint
        },
        { status: 500 }
      );
    }

    console.log('[Create Auth URL] State saved successfully:', insertData);

    // Build OAuth URL
    const authUrl = new URL(GOOGLE_AUTH_URL);
    authUrl.searchParams.set('client_id', clientId);
    authUrl.searchParams.set('redirect_uri', redirectUri);
    authUrl.searchParams.set('response_type', 'code');
    authUrl.searchParams.set('scope', SCOPES.join(' '));
    authUrl.searchParams.set('access_type', 'offline');
    authUrl.searchParams.set('prompt', 'consent');
    authUrl.searchParams.set('include_granted_scopes', 'true');
    authUrl.searchParams.set('state', state);

    const authUrlString = authUrl.toString();
    console.log('[Create Auth URL] Auth URL created successfully');
    console.log('[Create Auth URL] Redirect URI:', redirectUri);

    return NextResponse.json({
      authUrl: authUrlString,
      url: authUrlString, // For backward compatibility
    });

  } catch (error: any) {
    console.error('[Create Auth URL] Unexpected error:', error);
    return NextResponse.json(
      { error: error.message || 'Failed to create authorization URL' },
      { status: 500 }
    );
  }
}

app/api/gmb/sync/route.ts.content:
import { NextRequest, NextResponse } from 'next/server';
import { createClient } from '@/lib/supabase/server';

export const dynamic = 'force-dynamic';

const GOOGLE_TOKEN_URL = 'https://oauth2.googleapis.com/token';
const GBP_LOC_BASE = 'https://mybusinessbusinessinformation.googleapis.com/v1';
const GBP_V4_BASE = 'https://mybusiness.googleapis.com/v4';

// Helper function for chunking arrays
const chunks = <T>(array: T[], size = 100): T[][] => {
  return Array.from({ length: Math.ceil(array.length / size) }, (_, i) =>
    array.slice(i * size, i * size + size)
  );
};

// Refresh Google access token
async function refreshAccessToken(refreshToken: string): Promise<{
  access_token: string;
  expires_in: number;
  refresh_token?: string;
}> {
  console.log('[GMB Sync] Attempting to refresh access token...');

  const clientId = process.env.GOOGLE_CLIENT_ID;
  const clientSecret = process.env.GOOGLE_CLIENT_SECRET;

  if (!clientId || !clientSecret) {
    throw new Error('Missing Google OAuth configuration');
  }

  const response = await fetch(GOOGLE_TOKEN_URL, {
    method: 'POST',
    headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
    body: new URLSearchParams({
      grant_type: 'refresh_token',
      refresh_token: refreshToken,
      client_id: clientId,
      client_secret: clientSecret,
    }),
  });

  const data = await response.json();

  if (!response.ok) {
    console.error('[GMB Sync] Token refresh failed:', data);
    if (data.error === 'invalid_grant') {
      throw new Error('invalid_grant');
    }
    throw new Error(`Token refresh failed: ${data.error || 'Unknown error'}`);
  }

  console.log('[GMB Sync] Access token refreshed successfully');
  return data;
}

// Get valid access token (refresh if needed)
async function getValidAccessToken(
  supabase: any,
  accountId: string
): Promise<string> {
  console.log('[GMB Sync] Getting valid access token for account:', accountId);

  const { data: account, error } = await supabase
    .from('gmb_accounts')
    .select('access_token, refresh_token, token_expires_at')
    .eq('id', accountId)
    .single();

  if (error || !account) {
    console.error('[GMB Sync] Failed to fetch account:', error);
    throw new Error('Account not found');
  }

  const now = new Date();
  const expiresAt = account.token_expires_at ? new Date(account.token_expires_at) : null;

  // Check if token is still valid (with 5 minute buffer)
  if (account.access_token && expiresAt && expiresAt > new Date(now.getTime() + 5 * 60000)) {
    console.log('[GMB Sync] Using existing valid access token');
    return account.access_token;
  }

  // Token expired or missing, refresh it
  if (!account.refresh_token) {
    console.error('[GMB Sync] No refresh token available');
    throw new Error('No refresh token available - reconnect required');
  }

  console.log('[GMB Sync] Token expired or missing, refreshing...');
  const tokens = await refreshAccessToken(account.refresh_token);

  // Update tokens in database
  const newExpiresAt = new Date();
  newExpiresAt.setSeconds(newExpiresAt.getSeconds() + tokens.expires_in);

  const updateData: any = {
    access_token: tokens.access_token,
    token_expires_at: newExpiresAt.toISOString(),
  };

  if (tokens.refresh_token) {
    updateData.refresh_token = tokens.refresh_token;
  }

  const { error: updateError } = await supabase
    .from('gmb_accounts')
    .update(updateData)
    .eq('id', accountId);

  if (updateError) {
    console.error('[GMB Sync] Failed to update tokens:', updateError);
  }

  return tokens.access_token;
}

// Fetch locations from Google My Business
async function fetchLocations(
  accessToken: string,
  accountResource: string,
  pageToken?: string
): Promise<{ locations: any[]; nextPageToken?: string }> {
  console.log('[GMB Sync] Fetching locations for account:', accountResource);

  const url = new URL(`${GBP_LOC_BASE}/${accountResource}/locations`);
  url.searchParams.set('readMask', 'name,title,storefrontAddress,phoneNumbers,websiteUri,categories');
  url.searchParams.set('pageSize', '100');
  url.searchParams.set('alt', 'json');
  if (pageToken) {
    url.searchParams.set('pageToken', pageToken);
  }

  const response = await fetch(url.toString(), {
    headers: { 
      Authorization: `Bearer ${accessToken}`,
      Accept: 'application/json',
    },
  });

  // Check if response failed
  if (!response.ok) {
    // Try to read error as JSON if Content-Type is correct
    const contentType = response.headers.get('content-type')?.toLowerCase();
    let errorData: any = {};

    if (contentType && contentType.includes('application/json')) {
      try {
        errorData = await response.json();
      } catch (e) {
        // Failed to parse JSON, continue with empty object
        console.error('[GMB Sync] Failed to parse error response as JSON');
      }
    } else {
      // Not JSON, try to read as text for debugging
      try {
        const errorText = await response.text();
        console.error('[GMB Sync] Non-JSON error response:', errorText.substring(0, 200));
      } catch (e) {
        // Ignore text parsing errors
      }
    }

    console.error('[GMB Sync] Failed to fetch locations:', errorData);
    throw new Error(`Failed to fetch locations: ${errorData.error?.message || 'Unknown error'}`);
  }

  // Response is OK, verify Content-Type before parsing
  const contentType = response.headers.get('content-type')?.toLowerCase();
  if (!contentType || !contentType.includes('application/json')) {
    console.error('[GMB Sync] Unexpected content type for locations:', contentType);
    throw new Error('Unexpected response format from Google API');
  }

  const data = await response.json();

  console.log(`[GMB Sync] Fetched ${data.locations?.length || 0} locations`);
  return {
    locations: data.locations || [],
    nextPageToken: data.nextPageToken,
  };
}

// Fetch reviews for a location
async function fetchReviews(
  accessToken: string,
  locationResource: string,
  pageToken?: string
): Promise<{ reviews: any[]; nextPageToken?: string }> {
  const url = new URL(`${GBP_V4_BASE}/${locationResource}/reviews`);
  url.searchParams.set('pageSize', '50');
  url.searchParams.set('alt', 'json');
  if (pageToken) {
    url.searchParams.set('pageToken', pageToken);
  }

  const response = await fetch(url.toString(), {
    headers: { 
      Authorization: `Bearer ${accessToken}`,
      Accept: 'application/json',
    },
  });

  // Check if response failed
  if (!response.ok) {
    // Try to read error as JSON if Content-Type is correct
    const contentType = response.headers.get('content-type')?.toLowerCase();
    let errorData: any = {};

    if (contentType && contentType.includes('application/json')) {
      try {
        errorData = await response.json();
      } catch (e) {
        // Failed to parse JSON, continue with empty object
        console.error('[GMB Sync] Failed to parse error response as JSON');
      }
    } else {
      // Not JSON, try to read as text for debugging
      try {
        const errorText = await response.text();
        console.error('[GMB Sync] Non-JSON error response:', errorText.substring(0, 200));
      } catch (e) {
        // Ignore text parsing errors
      }
    }

    console.error('[GMB Sync] Failed to fetch reviews for location:', locationResource, errorData);
    // Don't throw error for reviews, just return empty array
    return { reviews: [], nextPageToken: undefined };
  }

  // Response is OK, verify Content-Type before parsing
  const contentType = response.headers.get('content-type')?.toLowerCase();
  if (!contentType || !contentType.includes('application/json')) {
    console.error('[GMB Sync] Unexpected content type for reviews:', contentType);
    return { reviews: [], nextPageToken: undefined };
  }

  const data = await response.json();

  return {
    reviews: data.reviews || [],
    nextPageToken: data.nextPageToken,
  };
}

