app/api/youtube/videos/route.ts.content:
import { NextResponse } from "next/server";
import { createClient } from "@/lib/supabase/server";

export const dynamic = "force-dynamic";

const CHANNELS_URL = "https://www.googleapis.com/youtube/v3/channels";
const PLAYLIST_ITEMS_URL = "https://www.googleapis.com/youtube/v3/playlistItems";
const VIDEOS_URL = "https://www.googleapis.com/youtube/v3/videos";

export async function GET() {
  try {
    const supabase = await createClient();
    const { data: { user } } = await supabase.auth.getUser();
    if (!user) return NextResponse.json({ error: "Unauthorized" }, { status: 401 });

    //   access_token  oauth_tokens
    const { data: tokenRow, error } = await supabase
      .from("oauth_tokens")
      .select("access_token")
      .eq("user_id", user.id)
      .eq("provider", "youtube")
      .maybeSingle();
    if (error || !tokenRow?.access_token) {
      return NextResponse.json({ items: [] });
    }

    //   uploads playlistId
    const chRes = await fetch(`${CHANNELS_URL}?part=contentDetails&mine=true&maxResults=1&alt=json`, {
      headers: { Authorization: `Bearer ${tokenRow.access_token}`, Accept: "application/json" },
    });
    const chJson = await chRes.json().catch(() => ({}));
    const uploads = chJson?.items?.[0]?.contentDetails?.relatedPlaylists?.uploads;
    if (!uploads) return NextResponse.json({ items: [] });

    //     ( 8)
    const plRes = await fetch(
      `${PLAYLIST_ITEMS_URL}?part=snippet,contentDetails&playlistId=${encodeURIComponent(uploads)}&maxResults=8&alt=json`,
      { headers: { Authorization: `Bearer ${tokenRow.access_token}`, Accept: "application/json" } }
    );
    const plJson = await plRes.json().catch(() => ({}));
    const videoIds = (plJson?.items || [])
      .map((it: any) => it?.contentDetails?.videoId)
      .filter(Boolean)
      .join(",");

    if (!videoIds) return NextResponse.json({ items: [] });

    //    (///)
    const vidsRes = await fetch(
      `${VIDEOS_URL}?part=snippet,statistics&id=${encodeURIComponent(videoIds)}&maxResults=8&alt=json`,
      { headers: { Authorization: `Bearer ${tokenRow.access_token}`, Accept: "application/json" } }
    );
    const vidsJson = await vidsRes.json().catch(() => ({}));
    const items =
      (vidsJson?.items || []).map((v: any) => ({
        id: v?.id,
        title: v?.snippet?.title || "Untitled",
        thumbnail:
          v?.snippet?.thumbnails?.medium?.url ||
          v?.snippet?.thumbnails?.default?.url ||
          "",
        views: Number(v?.statistics?.viewCount || 0),
        publishedAt: v?.snippet?.publishedAt || new Date().toISOString(),
        url: v?.id ? `https://www.youtube.com/watch?v=${v.id}` : "#",
      })) || [];

    return NextResponse.json({ items });
  } catch (e: any) {
    return NextResponse.json({ error: e.message || "Failed to load videos" }, { status: 500 });
  }
}

app/api/youtube/oauth-callback/route.ts.content:
import { NextRequest, NextResponse } from "next/server";
import { createClient, createAdminClient } from "@/lib/supabase/server";

export const dynamic = "force-dynamic";

const TOKEN_URL = "https://oauth2.googleapis.com/token";
const USERINFO_URL = "https://www.googleapis.com/oauth2/v2/userinfo";
const CHANNELS_URL = "https://www.googleapis.com/youtube/v3/channels";

export async function GET(req: NextRequest) {
  try {
    const searchParams = req.nextUrl.searchParams;
    const code = searchParams.get("code");
    const state = searchParams.get("state");
    const error = searchParams.get("error");

    const baseUrl = process.env.NEXT_PUBLIC_BASE_URL || "https://nnh.ae";

    if (error) {
      return NextResponse.redirect(
        `${baseUrl}/youtube-dashboard#error=${encodeURIComponent(error)}`
      );
    }
    if (!code || !state) {
      return NextResponse.redirect(
        `${baseUrl}/youtube-dashboard#error=${encodeURIComponent("Missing code/state")}`
      );
    }

    const supabase = await createClient();
    const { data: stateRecord, error: stateErr } = await supabase
      .from("oauth_states")
      .select("*")
      .eq("state", state)
      .eq("used", false)
      // .eq("provider", "youtube") //     
      .single();

    if (stateErr || !stateRecord) {
      return NextResponse.redirect(
        `${baseUrl}/youtube-dashboard#error=${encodeURIComponent("Invalid or expired state")}`
      );
    }

    const exp = new Date(stateRecord.expires_at);
    if (exp < new Date()) {
      return NextResponse.redirect(
        `${baseUrl}/youtube-dashboard#error=${encodeURIComponent("State expired")}`
      );
    }

    await supabase.from("oauth_states").update({ used: true }).eq("state", state);

    const clientId = process.env.YT_CLIENT_ID || process.env.GOOGLE_CLIENT_ID;
    const clientSecret =
      process.env.YT_CLIENT_SECRET || process.env.GOOGLE_CLIENT_SECRET;
    const redirectUri =
      process.env.YT_REDIRECT_URI ||
      `${process.env.NEXT_PUBLIC_BASE_URL}/api/youtube/oauth-callback`;

    if (!clientId || !clientSecret || !redirectUri) {
      return NextResponse.redirect(
        `${baseUrl}/youtube-dashboard#error=${encodeURIComponent("Server config error")}`
      );
    }

    const tokenRes = await fetch(TOKEN_URL, {
      method: "POST",
      headers: { "Content-Type": "application/x-www-form-urlencoded" },
      body: new URLSearchParams({
        code,
        client_id: clientId,
        client_secret: clientSecret,
        redirect_uri: redirectUri,
        grant_type: "authorization_code",
      }),
    });

    const tokenData = await tokenRes.json();
    if (!tokenRes.ok) {
      return NextResponse.redirect(
        `${baseUrl}/youtube-dashboard#error=${encodeURIComponent(
          tokenData.error_description || tokenData.error || "Token exchange failed"
        )}`
      );
    }

    const userinfoRes = await fetch(`${USERINFO_URL}?alt=json`, {
      headers: {
        Authorization: `Bearer ${tokenData.access_token}`,
        Accept: "application/json",
      },
    });
    const userinfo = await userinfoRes.json().catch(() => ({}));

    const channelRes = await fetch(
      `${CHANNELS_URL}?part=id,snippet,statistics&mine=true&maxResults=1&alt=json`,
      {
        headers: {
          Authorization: `Bearer ${tokenData.access_token}`,
          Accept: "application/json",
        },
      }
    );
    const channelJson = await channelRes.json().catch(() => ({}));
    const channel = Array.isArray(channelJson?.items) ? channelJson.items[0] : null;

    const admin = createAdminClient();
    const now = new Date();
    const expiresAt = new Date(
      now.getTime() + (tokenData.expires_in || 3600) * 1000
    ).toISOString();

    const { error: upErr } = await admin
      .from("oauth_tokens")
      .upsert(
        {
          user_id: stateRecord.user_id,
          provider: "youtube",
          access_token: tokenData.access_token,
          refresh_token: tokenData.refresh_token || null,
          token_expires_at: expiresAt,
          account_id: channel?.id || null,
          metadata: {
            email: userinfo?.email || null,
            channel_title: channel?.snippet?.title || null,
            statistics: channel?.statistics || null,
          },
          updated_at: new Date().toISOString(),
        },
        { onConflict: "user_id,provider" }
      );

    if (upErr) {
      return NextResponse.redirect(
        `${baseUrl}/youtube-dashboard#error=${encodeURIComponent("Failed to save tokens")}`
      );
    }

    return NextResponse.redirect(`${baseUrl}/youtube-dashboard#success=true`);
  } catch (e: any) {
    const baseUrl = process.env.NEXT_PUBLIC_BASE_URL || "https://nnh.ae";
    return NextResponse.redirect(
      `${baseUrl}/youtube-dashboard#error=${encodeURIComponent(
        e.message || "Unexpected error"
      )}`
    );
  }
}

app/api/youtube/create-auth-url/route.ts.content:
import { NextRequest, NextResponse } from "next/server";
import { createClient, createAdminClient } from "@/lib/supabase/server";

export const dynamic = "force-dynamic";

const GOOGLE_AUTH_URL = "https://accounts.google.com/o/oauth2/v2/auth";
const SCOPES = [
  "https://www.googleapis.com/auth/youtube.readonly",
  "https://www.googleapis.com/auth/userinfo.email",
  "https://www.googleapis.com/auth/userinfo.profile",
  "openid",
];

export async function POST(_req: NextRequest) {
  try {
    const supabase = await createClient();
    const {
      data: { user },
      error: authError,
    } = await supabase.auth.getUser();

    if (authError || !user) {
      return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
    }

    const clientId = process.env.YT_CLIENT_ID || process.env.GOOGLE_CLIENT_ID;
    const redirectUri =
      process.env.YT_REDIRECT_URI ||
      `${process.env.NEXT_PUBLIC_BASE_URL}/api/youtube/oauth-callback`;

    if (!clientId || !redirectUri) {
      return NextResponse.json(
        { error: "Server configuration error (YouTube OAuth)" },
        { status: 500 }
      );
    }

    const state = crypto.randomUUID();
    const expiresAt = new Date();
    expiresAt.setMinutes(expiresAt.getMinutes() + 30);

    const admin = createAdminClient();
    const { error: stateErr } = await admin.from("oauth_states").insert({
      state,
      user_id: user.id,
      // provider: "youtube", //     
      expires_at: expiresAt.toISOString(),
      used: false,
    });
    if (stateErr) {
      return NextResponse.json(
        { error: "Failed to persist OAuth state" },
        { status: 500 }
      );
    }

    const url = new URL(GOOGLE_AUTH_URL);
    url.searchParams.set("client_id", clientId);
    url.searchParams.set("redirect_uri", redirectUri);
    url.searchParams.set("response_type", "code");
    url.searchParams.set("scope", SCOPES.join(" "));
    url.searchParams.set("access_type", "offline");
    url.searchParams.set("include_granted_scopes", "true");
    url.searchParams.set("prompt", "consent");
    url.searchParams.set("state", state);

    const authUrl = url.toString();
    return NextResponse.json({ authUrl, url: authUrl });
  } catch (e: any) {
    return NextResponse.json(
      { error: e.message || "Failed to create YouTube auth URL" },
      { status: 500 }
    );
  }
}

