"use client"

import { Card, CardContent } from "@/components/ui/card"
import { motion } from "framer-motion"
import type { LucideIcon } from "lucide-react"
import { useEffect, useState } from "react"

interface StatCardProps {
  title: string
  value: string | number
  change?: string
  changeType?: "positive" | "negative" | "neutral"
  icon: LucideIcon
  index: number
}

export function StatCard({ title, value, change, changeType = "neutral", icon: Icon, index }: StatCardProps) {
  const [displayValue, setDisplayValue] = useState(0)
  const numericValue = typeof value === "string" ? Number.parseFloat(value) : value

  useEffect(() => {
    if (typeof numericValue === "number" && !isNaN(numericValue)) {
      let start = 0
      const end = numericValue
      const duration = 1000
      const increment = end / (duration / 16)

      const timer = setInterval(() => {
        start += increment
        if (start >= end) {
          setDisplayValue(end)
          clearInterval(timer)
        } else {
          setDisplayValue(start)
        }
      }, 16)

      return () => clearInterval(timer)
    }
  }, [numericValue])

  const changeColor =
    changeType === "positive" ? "text-green-500" : changeType === "negative" ? "text-red-500" : "text-muted-foreground"

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5, delay: index * 0.1 }}
    >
      <Card className="bg-card border-primary/30 hover:border-primary/50 transition-all duration-300 hover:shadow-lg hover:shadow-primary/20">
        <CardContent className="p-6">
          <div className="flex items-center justify-between">
            <div className="space-y-2">
              <p className="text-sm font-medium text-muted-foreground">{title}</p>
              <p className="text-3xl font-bold text-foreground">
                {typeof value === "string" ? value : Math.round(displayValue).toLocaleString()}
              </p>
              {change && <p className={`text-xs font-medium ${changeColor}`}>{change}</p>}
            </div>
            <div className="w-12 h-12 rounded-lg bg-primary/20 flex items-center justify-center">
              <Icon className="w-6 h-6 text-primary" />
            </div>
          </div>
        </CardContent>
      </Card>
    </motion.div>
  )
}

app/auth/callback/route.ts.content:
import { createClient } from '@/lib/supabase/server'
import { NextResponse } from 'next/server'

function getOriginFromRequest(request: Request): string {
  // Allow explicit override via environment variable
  if (process.env.NEXT_PUBLIC_BASE_URL) {
    return process.env.NEXT_PUBLIC_BASE_URL
  }

  const requestUrl = new URL(request.url)

  // Get host from headers (respects reverse proxy/CDN)
  const forwardedHost = request.headers.get('x-forwarded-host')
  const forwardedProto = request.headers.get('x-forwarded-proto')
  const host = request.headers.get('host') || requestUrl.host

  // Determine protocol: prefer forwarded header, then request URL protocol
  const proto = forwardedProto || requestUrl.protocol.replace(':', '')

  // Use forwarded host if available, otherwise host header
  const finalHost = forwardedHost || host

  return `${proto}://${finalHost}`
}

export async function GET(request: Request) {
  const requestUrl = new URL(request.url)
  const code = requestUrl.searchParams.get('code')
  const state = requestUrl.searchParams.get('state')
  const baseUrl = getOriginFromRequest(request)

  // Handle OAuth callback from Google (GMB) - check state FIRST
  // Google OAuth sends both code AND state, so we check state first
  if (state) {
    // GMB OAuth is handled by /api/gmb/oauth-callback directly
    // This route should not be used for GMB OAuth
    // Redirect to the Next.js API route instead
    return NextResponse.redirect(`${baseUrl}/api/gmb/oauth-callback${requestUrl.search}`)
  }

  // Handle Supabase auth callback (only code, no state)
  if (code) {
    const supabase = await createClient()

    // Exchange code for session
    const { data, error } = await supabase.auth.exchangeCodeForSession(code)

    if (error) {
      return NextResponse.redirect(`${baseUrl}/auth/login?error=${encodeURIComponent(error.message)}`)
    }

    // Redirect to dashboard with success
    return NextResponse.redirect(`${baseUrl}/dashboard`)
  }

  return NextResponse.redirect(baseUrl)
}

components/ai-studio/content-generator.t.content:
"use client"

import { useState } from "react"
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card"
import { Button } from "@/components/ui/button"
import { Textarea } from "@/components/ui/textarea"
import { Label } from "@/components/ui/label"
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select"
import { Badge } from "@/components/ui/badge"
import { Loader2, Sparkles, Copy, Download, RefreshCw } from "lucide-react"
import { motion, AnimatePresence } from "framer-motion"

interface ContentGeneratorProps {
  contentType: string
}

const tones = [
  { value: "professional", label: "Professional" },
  { value: "friendly", label: "Friendly" },
  { value: "casual", label: "Casual" },
  { value: "formal", label: "Formal" },
  { value: "enthusiastic", label: "Enthusiastic" },
]

const providers = [
  { value: "openai", label: "OpenAI GPT-4" },
  { value: "anthropic", label: "Anthropic Claude" },
  { value: "groq", label: "Groq" },
]

export function ContentGenerator({ contentType }: ContentGeneratorProps) {
  const [prompt, setPrompt] = useState("")
  const [tone, setTone] = useState("professional")
  const [provider, setProvider] = useState("openai")
  const [generating, setGenerating] = useState(false)
  const [generatedContent, setGeneratedContent] = useState("")
  const [copied, setCopied] = useState(false)

  const handleGenerate = async () => {
    setGenerating(true)
    setGeneratedContent("")

    try {
      // Simulate AI generation (in production, this would call an AI API)
      await new Promise((resolve) => setTimeout(resolve, 2000))

      const content = generateMockContent(contentType, prompt, tone)
      setGeneratedContent(content)
    } catch (error) {
      console.error("Error generating content:", error)
    } finally {
      setGenerating(false)
    }
  }

  const generateMockContent = (type: string, userPrompt: string, selectedTone: string) => {
    const tonePrefix =
      selectedTone === "professional"
        ? "We are pleased to announce"
        : selectedTone === "friendly"
          ? "Hey there! We're excited to share"
          : selectedTone === "casual"
            ? "Check this out!"
            : selectedTone === "formal"
              ? "It is with great pleasure that we inform you"
              : "We're thrilled to let you know"

    switch (type) {
      case "posts":
        return `${tonePrefix} that ${userPrompt || "we have something special for you"}! Visit us today to experience the difference. #LocalBusiness #CommunityFirst`
      case "responses":
        return `Thank you for your feedback! ${userPrompt || "We truly appreciate your business and look forward to serving you again soon."}`
      case "descriptions":
        return `${userPrompt || "Welcome to our business"} - where quality meets excellence. We pride ourselves on delivering exceptional service to our valued customers.`
      case "faqs":
        return `Q: ${userPrompt || "What are your hours?"}\n\nA: We're here to serve you! Our team is available during business hours to assist with all your needs. Please contact us for specific timing.`
      default:
        return `${tonePrefix} - ${userPrompt || "Generated content will appear here"}`
    }
  }

  const handleCopy = () => {
    navigator.clipboard.writeText(generatedContent)
    setCopied(true)
    setTimeout(() => setCopied(false), 2000)
  }

  const handleDownload = () => {
    const blob = new Blob([generatedContent], { type: "text/plain" })
    const url = URL.createObjectURL(blob)
    const a = document.createElement("a")
    a.href = url
    a.download = `${contentType}-${Date.now()}.txt`
    a.click()
    URL.revokeObjectURL(url)
  }

  return (
    <div className="grid gap-6 lg:grid-cols-2">
      {/* Input Section */}
      <Card className="bg-card border-primary/30">
        <CardHeader>
          <CardTitle className="text-foreground flex items-center gap-2">
            <Sparkles className="w-5 h-5 text-primary" />
            Generate Content
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          {/* Provider Selection */}
          <div className="space-y-2">
            <Label className="text-foreground">AI Provider</Label>
            <Select value={provider} onValueChange={setProvider}>
              <SelectTrigger className="bg-secondary border-primary/30 text-foreground">
                <SelectValue />
              </SelectTrigger>
              <SelectContent className="bg-card border-primary/30">
                {providers.map((p) => (
                  <SelectItem key={p.value} value={p.value}>
                    {p.label}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          {/* Tone Selection */}
          <div className="space-y-2">
            <Label className="text-foreground">Tone & Style</Label>
            <div className="flex flex-wrap gap-2">
              {tones.map((t) => (
                <Badge
                  key={t.value}
                  variant={tone === t.value ? "default" : "outline"}
                  className={
                    tone === t.value
                      ? "bg-gradient-to-r from-primary to-accent text-white cursor-pointer"
                      : "border-primary/30 text-muted-foreground cursor-pointer hover:border-primary/50"
                  }
                  onClick={() => setTone(t.value)}
                >
                  {t.label}
                </Badge>
              ))}
            </div>
          </div>

          {/* Prompt Input */}
          <div className="space-y-2">
            <Label className="text-foreground">Your Prompt</Label>
            <Textarea
              value={prompt}
              onChange={(e) => setPrompt(e.target.value)}
              placeholder={`Describe what you want to create for ${contentType}...`}
              className="bg-secondary border-primary/30 text-foreground resize-none min-h-[200px]"
            />
          </div>

          {/* Generate Button */}
          <Button
            onClick={handleGenerate}
            disabled={generating}
            className="w-full bg-gradient-to-r from-primary to-accent hover:from-primary/90 hover:to-accent/90 text-white"
          >
            {generating ? (
              <>
                <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                Generating...
              </>
            ) : (
              <>
                <Sparkles className="mr-2 h-4 w-4" />
                Generate Content
              </>
            )}
          </Button>
        </CardContent>
      </Card>

      {/* Output Section */}
      <Card className="bg-card border-primary/30">
        <CardHeader>
          <CardTitle className="text-foreground">Generated Content</CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <AnimatePresence mode="wait">
            {generating ? (
              <motion.div
                key="loading"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                exit={{ opacity: 0 }}
                className="flex flex-col items-center justify-center py-16 space-y-4"
              >
                <div className="w-16 h-16 rounded-full bg-gradient-to-r from-primary to-accent animate-pulse" />
                <p className="text-muted-foreground">Generating your content...</p>
              </motion.div>
            ) : generatedContent ? (
              <motion.div
                key="content"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                className="space-y-4"
              >
                <div className="p-4 rounded-lg bg-secondary border border-primary/20 min-h-[200px]">
                  <p className="text-foreground whitespace-pre-wrap">{generatedContent}</p>
                </div>

                {/* Action Buttons */}
                <div className="flex gap-2">
                  <Button
                    onClick={handleCopy}
                    variant="outline"
                    className="flex-1 border-primary/30 text-foreground hover:bg-primary/20 bg-transparent"
                  >
                    <Copy className="mr-2 h-4 w-4" />
                    {copied ? "Copied!" : "Copy"}
                  </Button>
                  <Button
                    onClick={handleDownload}
                    variant="outline"
                    className="flex-1 border-primary/30 text-foreground hover:bg-primary/20 bg-transparent"
                  >
                    <Download className="mr-2 h-4 w-4" />
                    Download
                  </Button>
                  <Button
                    onClick={handleGenerate}
                    variant="outline"
                    className="flex-1 border-primary/30 text-foreground hover:bg-primary/20 bg-transparent"
                  >
                    <RefreshCw className="mr-2 h-4 w-4" />
                    Regenerate
                  </Button>
                </div>
              </motion.div>
            ) : (
              <motion.div
                key="empty"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                exit={{ opacity: 0 }}
                className="flex flex-col items-center justify-center py-16 text-center"
              >
                <div className="w-16 h-16 rounded-full bg-primary/20 flex items-center justify-center mb-4">
                  <Sparkles className="w-8 h-8 text-primary" />
                </div>
                <p className="text-muted-foreground">Your generated content will appear here</p>
              </motion.div>
            )}
          </AnimatePresence>
        </CardContent>
      </Card>
    </div>
  )
}

