// lib/hooks/useAccountsManagement.ts
import { useState, useCallback } from 'react';
import { createClient } from '@/lib/supabase/client';
import { useToast } from '@/hooks/use-toast';
import type { GmbAccount } from '@/lib/types/database'; //    

export function useAccountsManagement() {
  const [accounts, setAccounts] = useState<GmbAccount[]>([]);
  const [loading, setLoading] = useState(true);
  const [syncing, setSyncing] = useState<string | null>(null);
  const [deleting, setDeleting] = useState<string | null>(null);
  const { toast } = useToast();
  const supabase = createClient();

  const fetchAccounts = useCallback(async (): Promise<GmbAccount[]> => { //   
    setLoading(true);
    console.log('[useAccountsManagement] Fetching accounts...');
    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) {
        console.log('[useAccountsManagement] No authenticated user found.');
        setAccounts([]);
        return [];
      }
       console.log('[useAccountsManagement] Authenticated user ID:', user.id);

      const { data: accountsData, error } = await supabase
        .from('gmb_accounts')
        .select('*')
        .eq('user_id', user.id)
        .order('created_at', { ascending: false });

      if (error) throw error;

      console.log(`[useAccountsManagement] Found ${accountsData?.length ?? 0} accounts in DB.`);

      if (!accountsData || accountsData.length === 0) {
        setAccounts([]);
        return [];
      }

      console.log('[useAccountsManagement] Fetching location counts...');
      const accountsWithLocations = await Promise.all(
        accountsData.map(async (account) => {
          // Add a check for account.id existence
          if (!account.id) {
             console.warn('[useAccountsManagement] Account found without ID:', account);
             // Return a partial object or skip, ensure GmbAccount type compatibility
             return { ...account, id: `unknown-${Math.random()}`, total_locations: 0 } as GmbAccount;
          }
          const { count, error: countError } = await supabase
            .from('gmb_locations')
            .select('*', { count: 'exact', head: true })
            .eq('gmb_account_id', account.id);

          if (countError) {
             console.error(`[useAccountsManagement] Error fetching location count for account ${account.id}:`, countError);
             // Return account data even if count fails
             return { ...account, total_locations: 0 };
          }

          return {
            ...account,
            total_locations: count || 0,
          };
        })
      );

      // Filter out potential error objects if needed, ensuring type safety
      const validAccounts = accountsWithLocations.filter(acc => acc.id) as GmbAccount[];

      console.log('[useAccountsManagement] Accounts processed:', validAccounts);
      setAccounts(validAccounts);
      return validAccounts; // Return the valid accounts
    } catch (error: any) {
      console.error('[useAccountsManagement] Error fetching accounts:', error);
      toast({
        title: 'Error Loading Accounts',
        description: error.message || 'Failed to fetch accounts',
        variant: 'destructive',
      });
      setAccounts([]);
      return []; // Return empty array on error
    } finally {
      setLoading(false);
      console.log('[useAccountsManagement] fetchAccounts finished.');
    }
  }, [supabase, toast]); // Removed fetchAccounts from dependencies

  const handleSync = useCallback(async (accountId: string, isAutoSync = false) => {
    setSyncing(accountId);
    console.log(`[useAccountsManagement] ${isAutoSync ? 'Auto-syncing' : 'Syncing'} account ${accountId}`);
    try {
      const response = await fetch('/api/gmb/sync', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ accountId, syncType: 'full' }),
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        console.error('[useAccountsManagement] Sync API error:', errorData);
        if (errorData.error === 'invalid_grant') {
          toast({
            title: 'Authorization Expired',
            description: 'Please reconnect your account.',
            variant: 'destructive',
          });
          // Attempt to refetch accounts to update status UI
           await fetchAccounts();
          return;
        }
        throw new Error(errorData.error || errorData.message || 'Sync failed');
      }

      const data = await response.json();
      console.log('[useAccountsManagement] Sync successful:', data);
      toast({
        title: isAutoSync ? 'Auto-Sync Complete!' : 'Sync Successful!',
        description: `Synced ${data.counts?.locations || 0} locations, ${data.counts?.reviews || 0} reviews, ${data.counts?.media || 0} media items.`,
      });
      await fetchAccounts(); // Refresh list after sync
    } catch (error: any) {
      console.error('[useAccountsManagement] Sync error:', error);
      toast({
        title: 'Sync Failed',
        description: error.message || 'Failed to sync account',
        variant: 'destructive',
      });
    } finally {
      setSyncing(null);
    }
  }, [supabase, toast, fetchAccounts]); // Added supabase

  const handleDisconnect = useCallback(async (accountId: string) => {
    // Added confirmation message in Arabic
    if (!confirm('               .')) return;

    setDeleting(accountId);
    console.log(`[useAccountsManagement] Disconnecting account ${accountId}`);
    try {
      // It's better practice to check the user ID here if RLS isn't fully guaranteed or for defense in depth
      const { data: { user } } = await supabase.auth.getUser();
       if (!user) throw new Error("User not authenticated for disconnect");

      const { error } = await supabase
        .from('gmb_accounts')
        .update({ is_active: false })
        .eq('id', accountId)
        .eq('user_id', user.id); // Ensure only the owner can disconnect

      if (error) throw error;

      toast({
        title: 'Account Disconnected', // Kept English for consistency with other toasts
        description: 'Syncing has been stopped for this account.',
      });
      await fetchAccounts(); // Refresh the list
    } catch (error: any) {
      console.error('[useAccountsManagement] Disconnect error:', error);
      toast({
        title: 'Error Disconnecting',
        description: error.message || 'Failed to disconnect account',
        variant: 'destructive',
      });
    } finally {
      setDeleting(null);
    }
  }, [supabase, toast, fetchAccounts]); // Added supabase

  return {
    accounts,
    loading,
    syncing,
    deleting,
    fetchAccounts,
    handleSync,
    handleDisconnect,
  };
}

components/accounts/AccountCard.tsx.content:
"use client"

import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Building2, MapPin, Clock, RefreshCw, Trash2 } from "lucide-react";
import { motion } from "framer-motion";
import type { GmbAccount } from '@/lib/types/database';

interface AccountCardProps {
  account: GmbAccount;
  syncingAccountId: string | null;
  deletingAccountId: string | null;
  onSync: (accountId: string) => void;
  onDisconnect: (accountId: string) => void;
  formatDate: (dateString?: string | null) => string;
  index?: number;
}

export function AccountCard({
  account,
  syncingAccountId,
  deletingAccountId,
  onSync,
  onDisconnect,
  formatDate,
  index = 0,
}: AccountCardProps) {
  if (!account || !account.id) {
    console.error("AccountCard received invalid account data", account);
    return null;
  }

  const isSyncing = syncingAccountId === account.id;
  const isDeleting = deletingAccountId === account.id;
  const isActive = account.is_active ?? false;
  const currentStatus = isActive ? 'active' : 'disconnected';

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.3, delay: index * 0.05 }}
      whileHover={{ y: -4, scale: 1.02 }}
      className="h-full"
    >
      <Card 
        data-testid={`account-card-${account.id}`} 
        className="bg-card border border-primary/30 shadow-sm transition-all duration-200 hover:shadow-lg hover:shadow-primary/20 hover:border-primary/50 flex flex-col h-full"
      >
        <CardHeader className="pb-4">
          <div className="flex items-start justify-between gap-2">
            <div className="flex items-center gap-3 min-w-0 flex-1">
              <motion.div 
                className="w-10 h-10 bg-primary/10 rounded-lg flex items-center justify-center flex-shrink-0"
                whileHover={{ rotate: 360 }}
                transition={{ duration: 0.6 }}
              >
                <Building2 className="w-5 h-5 text-primary" />
              </motion.div>
              <div className="min-w-0">
                <CardTitle className="text-base font-semibold text-foreground truncate">{account.account_name || 'Unnamed Account'}</CardTitle>
                <CardDescription className="text-xs text-muted-foreground truncate">{account.email || 'No email'}</CardDescription>
              </div>
            </div>
            <Badge
              variant={isActive ? 'default' : 'secondary'}
              className={`capitalize text-xs px-2 py-0.5 rounded-full transition-colors duration-200 ${
                isActive
                  ? 'bg-green-100 text-green-800 border border-green-200 dark:bg-green-900/40 dark:text-green-300 dark:border-green-700/50'
                  : 'bg-red-100 text-red-800 border border-red-200 dark:bg-red-900/30 dark:text-red-300 dark:border-red-700/50'
              }`}
            >
              {currentStatus}
            </Badge>
          </div>
        </CardHeader>
        <CardContent className="space-y-3 pt-0 flex-grow flex flex-col justify-between">
          <div className="grid grid-cols-2 gap-3">
            <motion.div 
              className="bg-secondary/40 rounded-md p-2 border border-primary/10 text-center"
              whileHover={{ scale: 1.05 }}
              transition={{ duration: 0.2 }}
            >
              <div className="flex items-center justify-center gap-1 mb-0.5 text-muted-foreground">
                <MapPin className="w-3 h-3" />
                <p className="text-xs font-medium">Locations</p>
              </div>
              <p className="text-lg font-bold text-foreground">{account.total_locations ?? 0}</p>
            </motion.div>
            <motion.div 
              className="bg-secondary/40 rounded-md p-2 border border-primary/10 text-center"
              whileHover={{ scale: 1.05 }}
              transition={{ duration: 0.2 }}
            >
              <div className="flex items-center justify-center gap-1 mb-0.5 text-muted-foreground">
                <Clock className="w-3 h-3" />
                <p className="text-xs font-medium">Last Sync</p>
              </div>
              <p className="text-xs font-medium text-foreground h-5 flex items-center justify-center">
                {formatDate(account.last_sync)}
              </p>
            </motion.div>
          </div>

          <div className="mt-auto space-y-2">
            {!isActive && (
              <p className="text-xs text-center text-orange-500/90 dark:text-orange-400/80 mb-2 px-2">
                Account disconnected. Reconnect to enable syncing and updates.
              </p>
            )}
            <div className="flex gap-2">
              <Button
                onClick={() => onSync(account.id)}
                disabled={isSyncing || !isActive || isDeleting}
                className="flex-1 transition-all duration-200 hover:scale-105"
                variant="outline"
                size="sm"
              >
                <RefreshCw className={`w-3.5 h-3.5 mr-1.5 ${isSyncing ? 'animate-spin' : ''}`} />
                {isSyncing ? 'Syncing...' : 'Sync Now'}
              </Button>
              <Button
                onClick={() => onDisconnect(account.id)}
                disabled={isDeleting || !isActive}
                variant="destructive"
                size="sm"
                className="flex-shrink-0 transition-all duration-200 hover:scale-105"
              >
                <Trash2 className="w-3.5 h-3.5 mr-1.5" />
                {isDeleting ? '...' : 'Disconnect'}
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>
    </motion.div>
  );
}

app/youtube-dashboard/page.tsx.content:
"use client";
import React, { useEffect, useMemo, useState } from "react";
import { Button } from "@/components/ui/button";
import { createClient } from "@/lib/supabase/client";
import { Sheet, SheetContent, SheetTrigger } from "@/components/ui/sheet";
import { toast } from "sonner";
import { 
  Menu, 
  X, 
  Youtube, 
  BarChart3, 
  Sparkles, 
  MessageSquare, 
  Settings as SettingsIcon, 
  RefreshCw, 
  LogOut,
  Eye,
  Calendar,
  ThumbsUp,
  Download,
  TrendingUp,
  Video,
  Users
} from "lucide-react";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  Filler,
} from 'chart.js';
import { Line, Bar } from 'react-chartjs-2';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  Filler
);

type YTStatistics = { subscriberCount?: string; viewCount?: string; videoCount?: string };
type YTMetadata = { email?: string | null; channel_title?: string | null; statistics?: YTStatistics | null };
type YTVideo = { id: string; title: string; thumbnail: string; views: number; publishedAt: string; url: string };
type YTComment = { id: string; author: string; text: string; likes: number; publishedAt: string; videoUrl: string };
type YTAnalytics = { lastUpdated: string; months: string[]; viewsPerMonth: number[]; videosPerMonth: number[]; totalViews: number; totalVideos: number };
type Draft = { id: string; title: string; description: string; hashtags: string; created_at: string };

export default function YoutubeDashboard() {
  const supabase = createClient();

  const [loading, setLoading] = useState(true);
  const [connecting, setConnecting] = useState(false);
  const [refreshing, setRefreshing] = useState(false);
  const [disconnecting, setDisconnecting] = useState(false);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  const [channelTitle, setChannelTitle] = useState<string | null>(null);
  const [channelEmail, setChannelEmail] = useState<string | null>(null);
  const [stats, setStats] = useState({ subs: 0, views: 0, videos: 0 });
  const [videos, setVideos] = useState<YTVideo[]>([]);
  const [comments, setComments] = useState<YTComment[]>([]);
  const [analytics, setAnalytics] = useState<YTAnalytics | null>(null);
  const [activeTab, setActiveTab] = useState<"dashboard"|"analytics"|"composer"|"comments"|"settings">("dashboard");

  // Composer state
  const [prompt, setPrompt] = useState("");
  const [tone, setTone] = useState<"neutral"|"friendly"|"professional"|"energetic">("neutral");
  const [genLoading, setGenLoading] = useState(false);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [hashtags, setHashtags] = useState("");
  const [saving, setSaving] = useState(false);
  const [drafts, setDrafts] = useState<Draft[]>([]);

  // Filters
  const [vSearch, setVSearch] = useState("");
  const [vFrom, setVFrom] = useState("");
  const [vTo, setVTo] = useState("");
  const [cSearch, setCSearch] = useState("");
  const [cFrom, setCFrom] = useState("");
  const [cTo, setCTo] = useState("");

  // Pagination
  const [commentsPage, setCommentsPage] = useState(1);
  const commentsPerPage = 10;

  // Safe fetch helpers
  const safeGet = async (url: string) => {
    const r = await fetch(url, { headers: { Accept: "application/json" } });
    const t = await r.text();
    let j: any = {};
    try { j = JSON.parse(t); } catch {}
    if (!r.ok) throw new Error(j.error || t.slice(0, 300) || `GET ${url} failed`);
    return j;
  };
  const safePost = async (url: string, body?: any) => {
    const r = await fetch(url, {
      method: "POST",
      headers: { "Content-Type": "application/json", Accept: "application/json" },
      body: body ? JSON.stringify(body) : undefined,
    });
    const t = await r.text();
    let j: any = {};
    try { j = JSON.parse(t); } catch {}
    if (!r.ok) throw new Error(j.error || t.slice(0, 300) || `POST ${url} failed`);
    return j;
  };
  const safeDelete = async (url: string) => {
    const r = await fetch(url, { method: "DELETE", headers: { Accept: "application/json" } });
    const t = await r.text();
    let j: any = {};
    try { j = JSON.parse(t); } catch {}
    if (!r.ok) throw new Error(j.error || t.slice(0, 300) || `DELETE ${url} failed`);
    return j;
  };

  // Data loaders
  const fetchFromDB = async () => {
    const { data: { user } } = await supabase.auth.getUser();
    if (!user) throw new Error("Please login first");
    const { data, error: qErr } = await supabase
      .from("oauth_tokens")
      .select("provider, account_id, metadata")
      .eq("user_id", user.id)
      .eq("provider", "youtube")
      .maybeSingle();
    if (qErr) throw qErr;
    if (!data) {
      setChannelTitle(null);
      setChannelEmail(null);
      setStats({ subs: 0, views: 0, videos: 0 });
      setVideos([]);
      setComments([]);
      setAnalytics(null);
      return false;
    }
    const meta = (data.metadata || {}) as YTMetadata;
    const s = meta.statistics || {};
    setChannelTitle(meta.channel_title || "YouTube Channel");
    setChannelEmail(meta.email || null);
    setStats({
      subs: Number(s.subscriberCount || 0),
      views: Number(s.viewCount || 0),
      videos: Number(s.videoCount || 0),
    });
    return true;
  };
  const fetchVideos = async () => {
    const j = await safeGet("/api/youtube/videos");
    setVideos(j.items || []);
  };
  const fetchComments = async () => {
    const j = await safeGet("/api/youtube/comments");
    setComments(j.items || []);
  };
  const fetchAnalytics = async () => {
    const j = await safeGet("/api/youtube/analytics");
    setAnalytics(j as YTAnalytics);
  };
  const fetchDrafts = async () => {
    const j = await safeGet("/api/youtube/composer/drafts");
    setDrafts(j.items || []);
  };

  // Actions
  const handleConnectYoutube = async () => {
    try {
      setConnecting(true);
      const data = await safePost("/api/youtube/create-auth-url", {});
      if (data.authUrl) window.location.href = data.authUrl;
    } catch (e: any) {
      toast.error(e.message || "Failed to start YouTube connection");
      setConnecting(false);
    }
  };
  const handleRefresh = async () => {
    try {
      setRefreshing(true);
      await safePost("/api/youtube/token/refresh-if-needed");
      await safePost("/api/youtube/refresh");
      await Promise.all([fetchFromDB(), fetchVideos(), fetchComments(), fetchAnalytics(), fetchDrafts()]);
      toast.success("Data refreshed successfully!");
    } catch (e: any) {
      toast.error(e.message || "Failed to update data");
    } finally {
      setRefreshing(false);
    }
  };
  const handleDisconnect = async () => {
    if (!confirm("Are you sure you want to disconnect YouTube?")) return;
    try {
      setDisconnecting(true);
      await safePost("/api/youtube/disconnect");
      setChannelTitle(null);
      setChannelEmail(null);
      setStats({ subs: 0, views: 0, videos: 0 });
      setVideos([]);
      setComments([]);
      setAnalytics(null);
      setDrafts([]);
      toast.success("YouTube disconnected successfully");
    } catch (e: any) {
      toast.error(e.message || "Failed to disconnect YouTube");
    } finally {
      setDisconnecting(false);
    }
  };

  const handleGenerate = async () => {
    try {
      setGenLoading(true);
      const res = await safePost("/api/youtube/composer/generate", { prompt, tone });
      setTitle(res.title || "");
      setDescription(res.description || "");
      setHashtags(res.hashtags || "");
      toast.success("Content generated successfully!");
    } catch (e: any) {
      toast.error(e.message || "Failed to generate content");
    } finally {
      setGenLoading(false);
    }
  };

  const handleSaveDraft = async () => {
    try {
      setSaving(true);
      const res = await safePost("/api/youtube/composer/drafts", { title, description, hashtags });
      if (res?.item) {
        setDrafts(d => [res.item, ...d]);
        toast.success("Draft saved successfully!");
      }
    } catch (e: any) {
      toast.error(e.message || "Failed to save draft");
    } finally {
      setSaving(false);
    }
  };

  const handleDeleteDraft = async (id: string) => {
    try {
      await safeDelete(`/api/youtube/composer/drafts?id=${encodeURIComponent(id)}`);
      setDrafts(d => d.filter(x => x.id !== id));
      toast.success("Draft deleted");
    } catch (e: any) {
      toast.error(e.message || "Failed to delete draft");
    }
  };

  // Init
  useEffect(() => {
    (async () => {
      try {
        setLoading(true);
        await safePost("/api/youtube/token/refresh-if-needed");
        const has = await fetchFromDB();
        if (has) {
          await Promise.all([fetchVideos(), fetchComments(), fetchAnalytics(), fetchDrafts()]);
        }
      } catch (e: any) {
        toast.error(e.message || "Failed to load channel data");
      } finally {
        setLoading(false);
      }
    })();
  }, []);

  // Filters
  const filteredVideos = useMemo(() => videos.filter(v => {
    const q = vSearch.trim().toLowerCase();
    const okQ = !q || v.title.toLowerCase().includes(q);
    const d = new Date(v.publishedAt).getTime();
    const okFrom = !vFrom || d >= new Date(vFrom).getTime();
    const okTo = !vTo || d <= new Date(vTo).getTime();
    return okQ && okFrom && okTo;
  }), [videos, vSearch, vFrom, vTo]);

  const filteredComments = useMemo(() => comments.filter(c => {
    const q = cSearch.trim().toLowerCase();
    const okQ = !q || c.text.toLowerCase().includes(q) || c.author.toLowerCase().includes(q);
    const d = new Date(c.publishedAt).getTime();
    const okFrom = !cFrom || d >= new Date(cFrom).getTime();
    const okTo = !cTo || d <= new Date(cTo).getTime();
    return okQ && okFrom && okTo;
  }), [comments, cSearch, cFrom, cTo]);

  // Paginated comments
  const paginatedComments = useMemo(() => {
    const start = (commentsPage - 1) * commentsPerPage;
    const end = start + commentsPerPage;
    return filteredComments.slice(start, end);
  }, [filteredComments, commentsPage]);

  const totalPages = Math.ceil(filteredComments.length / commentsPerPage);

  // CSV export
  const exportCSV = (rows: any[], headers: string[], filename: string) => {
    const esc = (s: any) => `"${String(s ?? "").replace(/"/g, '""')}"`;
    const csv = [headers.join(",")].concat(rows.map(r => headers.map(h => esc(r[h])).join(","))).join("\n");
    const blob = new Blob([csv], { type: "text/csv;charset=utf-8;" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = filename;
    a.click();
    URL.revokeObjectURL(url);
  };
  const exportVideosCSV = () => {
    exportCSV(
      filteredVideos.map(v => ({ id: v.id, title: v.title, views: v.views, publishedAt: v.publishedAt, url: v.url })),
      ["id", "title", "views", "publishedAt", "url"],
      "youtube_videos.csv"
    );
    toast.success("Videos exported to CSV");
  };
  const exportCommentsCSV = () => {
    exportCSV(
      filteredComments.map(c => ({ id: c.id, author: c.author, likes: c.likes, publishedAt: c.publishedAt, text: c.text.replace(/\n/g, " "), videoUrl: c.videoUrl })),
      ["id", "author", "likes", "publishedAt", "text", "videoUrl"],
      "youtube_comments.csv"
    );
    toast.success("Comments exported to CSV");
  };

  // Chart data
  const viewsChartData = useMemo(() => {
    if (!analytics) return null;
    const labels = analytics.months.map((m: string) => m.slice(0, 7));
    return {
      labels,
      datasets: [{
        label: "Views",
        data: analytics.viewsPerMonth,
        borderColor: "#FF6B00",
        backgroundColor: "rgba(255,107,0,0.15)",
        borderWidth: 2,
        tension: 0.3,
        fill: true,
        pointBackgroundColor: "#FF6B00",
        pointBorderColor: "#fff",
        pointBorderWidth: 2,
        pointRadius: 4,
        pointHoverRadius: 6,
      }]
    };
  }, [analytics]);

  const videosChartData = useMemo(() => {
    if (!analytics) return null;
    const labels = analytics.months.map((m: string) => m.slice(0, 7));
    return {
      labels,
      datasets: [{
        label: "Videos",
        data: analytics.videosPerMonth,
        backgroundColor: "rgba(255,107,0,0.35)",
        borderColor: "#FF6B00",
        borderWidth: 1,
        borderRadius: 6,
      }]
    };
  }, [analytics]);

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: { 
        labels: { 
          color: "#fff",
          font: { size: 12 }
        } 
      },
      tooltip: {
        backgroundColor: "rgba(17, 17, 17, 0.95)",
        titleColor: "#FF6B00",
        bodyColor: "#fff",
        borderColor: "#FF6B00",
        borderWidth: 1,
        padding: 12,
        displayColors: false,
      }
    },
    scales: {
      x: { 
        ticks: { color: "#aaa", font: { size: 11 } },
        grid: { color: "rgba(255,255,255,0.05)" }
      },
      y: { 
        ticks: { color: "#aaa", font: { size: 11 } }, 
        beginAtZero: true,
        grid: { color: "rgba(255,255,255,0.05)" }
      }
    }
  };

  // Sidebar content
  const SidebarContent = () => (
    <div className="h-full flex flex-col">
      <div className="text-neon-orange font-extrabold text-xl mb-6 flex items-center gap-2">
        <Youtube className="w-6 h-6" />
        NNH  YouTube
      </div>
      <nav className="space-y-2 flex-1">
        {[
          { id: "dashboard" as const, label: "Dashboard", icon: BarChart3 },
          { id: "analytics" as const, label: "Analytics", icon: TrendingUp },
          { id: "composer" as const, label: "Composer", icon: Sparkles },
          { id: "comments" as const, label: "Comments", icon: MessageSquare },
          { id: "settings" as const, label: "Settings", icon: SettingsIcon },
        ].map((t) => (
          <button
            key={t.id}
            onClick={() => {
              setActiveTab(t.id);
              setMobileMenuOpen(false);
            }}
            className={`w-full text-left rounded-lg px-3 py-2.5 transition-all duration-200 flex items-center gap-2 ${
              activeTab === t.id 
                ? "bg-neon-orange text-black font-bold shadow-lg" 
                : "hover:bg-white/10 text-white/80 hover:text-white"
            }`}
          >
            <t.icon className="w-4 h-4" />
            {t.label}
          </button>
        ))}
      </nav>
      <div className="mt-auto space-y-2 pt-4 border-t border-white/10">
        <Button 
          className="w-full bg-white/10 hover:bg-white/20 transition-all duration-200 flex items-center gap-2" 
          onClick={handleRefresh} 
          disabled={refreshing}
        >
          <RefreshCw className={`w-4 h-4 ${refreshing ? 'animate-spin' : ''}`} />
          {refreshing ? "Updating..." : "Refresh"}
        </Button>
        <Button 
          className="w-full bg-red-600 hover:bg-red-700 transition-all duration-200 flex items-center gap-2" 
          onClick={handleDisconnect} 
          disabled={disconnecting}
        >
          <LogOut className="w-4 h-4" />
          {disconnecting ? "Disconnecting..." : "Disconnect"}
        </Button>
      </div>
    </div>
  );

  // Loading skeleton
  const LoadingSkeleton = () => (
    <div className="space-y-6">
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {[1, 2, 3].map((i) => (
          <div key={i} className="rounded-2xl p-6 bg-white/5 h-32 relative overflow-hidden">
            <div className="absolute inset-0 shimmer"></div>
          </div>
        ))}
      </div>
      <div className="rounded-2xl p-6 bg-white/5 h-96 relative overflow-hidden">
        <div className="absolute inset-0 shimmer"></div>
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-black text-white">
      <div className="flex">
        {/* Desktop Sidebar */}
        <aside className="w-64 hidden md:block border-r border-white/10 min-h-screen p-5">
          <SidebarContent />
        </aside>

        {/* Mobile Menu */}
        <Sheet open={mobileMenuOpen} onOpenChange={setMobileMenuOpen}>
          <SheetTrigger asChild>
            <Button 
              className="fixed top-4 left-4 z-40 md:hidden bg-neon-orange hover:bg-orange-700"
              size="icon"
            >
              <Menu className="w-5 h-5" />
            </Button>
          </SheetTrigger>
          <SheetContent side="left" className="w-64 bg-black border-white/10 p-5">
            <SidebarContent />
          </SheetContent>
        </Sheet>

        <main className="flex-1 px-4 md:px-8 py-8">
          <div className="flex flex-col md:flex-row md:items-center justify-between mb-8 gap-4">
            <h1 className="text-3xl md:text-4xl font-extrabold text-neon-orange ml-14 md:ml-0">
              YouTube Studio
            </h1>
            <div className="flex gap-2 ml-14 md:ml-0">
              <Button 
                className="bg-white/10 hover:bg-white/20 transition-all duration-200 flex items-center gap-2" 
                onClick={handleRefresh} 
                disabled={refreshing}
              >
                <RefreshCw className={`w-4 h-4 ${refreshing ? 'animate-spin' : ''}`} />
                <span className="hidden sm:inline">{refreshing ? "Updating..." : "Refresh"}</span>
              </Button>
              <Button 
                className="bg-neon-orange hover:bg-orange-700 text-white font-bold transition-all duration-200 flex items-center gap-2" 
                onClick={handleConnectYoutube} 
                disabled={connecting}
              >
                <Youtube className="w-4 h-4" />
                <span className="hidden sm:inline">{connecting ? "Connecting..." : "Connect"}</span>
              </Button>
            </div>
          </div>

          {loading ? (
            <LoadingSkeleton />
          ) : (
            <>
              <div className="mb-6">
                {channelTitle ? (
                  <div className="text-lg text-white/80 flex items-center gap-2">
                    <Youtube className="w-5 h-5 text-neon-orange" />
                    Channel: <span className="font-semibold text-white">{channelTitle}</span>
                  </div>
